import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register"; // Ensure this import exists
import Settings from "./pages/Settings";
import Navbar from "./components/Navbar";
import { useState, useEffect } from "react";

function NoteViewer({ note, onUpdate }) {
  const [content, setContent] = useState(note.content);

  useEffect(() => {
    setContent(note.content); // Update content when the selected note changes
  }, [note]);

  return (
    <div className="flex-1 p-6">
      <div className="border rounded-lg p-4 bg-white shadow">
        <h2 className="text-2xl font-bold mb-2">{note.title}</h2>
        <textarea
          className="w-full h-64 border p-2 rounded mt-2"
          value={content}
          onChange={(e) => {
            setContent(e.target.value);
            onUpdate(note.id, e.target.value); // Update the note content
          }}
          placeholder="Start typing your note here..."
        />
      </div>
      <button
        onClick={() => onUpdate(note.id, content)}
        className="mt-4 p-2 bg-green-500 text-white rounded hover:bg-green-700 hover:scale-105 transition-transform"
      >
        Save Note
      </button>
    </div>
  );
}
