export default function NoteViewer({ note, onUpdate }) {
  const handleChange = (event) => {
    onUpdate(note._id, event.target.value); // Ensure function is called correctly
  };

  return (
    <div className="flex-1 p-6">
      <textarea
        className="w-full h-full border p-2"
        value={note.content}
        onChange={handleChange}
      />
    </div>
  );
}
