export default function Sidebar({ notes, onSelect, selectedNote }) {
  return (
    <div className="w-1/4 bg-gray-100 p-4">
      <h2 className="text-lg font-bold mb-4">Your Notes</h2>
      {notes.map((note) => (
        <div
          key={note.id}
          className={`p-2 mb-2 rounded cursor-pointer ${selectedNote?.id === note.id ? "bg-gray-300" : "bg-gray-200"} hover:scale-105 transition-transform`}
          onClick={() => onSelect(note)}
        >
          {note.title}
        </div>
      ))}
    </div>
  );
}
